import React, { useState } from "react";
import "./co"
const Contact = () => {

  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState(false);

  const validateForm = () => {
    let newErrors = {};

    if (!form.name.trim()) newErrors.name = "Full name is required";

    if (!form.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(form.email)) {
      newErrors.email = "Invalid email format";
    }

    if (!form.message.trim()) newErrors.message = "Message cannot be empty";

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      setSuccess(true);
      setForm({ name: "", email: "", message: "" });

      setTimeout(() => setSuccess(false), 3000);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 p-6">
      <div className="backdrop-blur-lg bg-white/20 shadow-2xl rounded-2xl p-8 w-full max-w-lg border border-white/30">

        <h1 className="text-4xl font-extrabold text-white text-center drop-shadow-lg">
          Contact Us
        </h1>

        <p className="text-center text-gray-200 mt-2 mb-6">
          We would love to hear from you ðŸ’¬
        </p>

        {success && (
          <div className="text-green-300 text-center mb-4 font-semibold">
            ðŸŽ‰ Message sent successfully!
          </div>
        )}

        <form className="space-y-6" onSubmit={handleSubmit}>
          
          <div>
            <label className="text-white font-medium">Full Name</label>
            <input
              type="text"
              name="name"
              placeholder="John Doe"
              value={form.name}
              onChange={handleChange}
              className="w-full px-4 py-3 mt-1 rounded-xl bg-white/30 border border-white/40 text-white placeholder-gray-200 focus:ring-4 focus:ring-pink-400 outline-none transition-all"
            />
            {errors.name && (
              <p className="text-red-400 text-sm mt-1">{errors.name}</p>
            )}
          </div>

          <div>
            <label className="text-white font-medium">Email</label>
            <input
              type="email"
              name="email"
              placeholder="example@email.com"
              value={form.email}
              onChange={handleChange}
              className="w-full px-4 py-3 mt-1 rounded-xl bg-white/30 border border-white/40 text-white placeholder-gray-200 focus:ring-4 focus:ring-blue-400 outline-none transition-all"
            />
            {errors.email && (
              <p className="text-red-400 text-sm mt-1">{errors.email}</p>
            )}
          </div>

          <div>
            <label className="text-white font-medium">Message</label>
            <textarea
              rows="4"
              name="message"
              placeholder="Write your message..."
              value={form.message}
              onChange={handleChange}
              className="w-full px-4 py-3 mt-1 rounded-xl bg-white/30 border border-white/40 text-white placeholder-gray-200 focus:ring-4 focus:ring-green-400 outline-none transition-all"
            ></textarea>
            {errors.message && (
              <p className="text-red-400 text-sm mt-1">{errors.message}</p>
            )}
          </div>

          <button
            type="submit"
            className="w-full bg-gradient-to-r from-indigo-500 to-pink-500 py-3 text-lg font-semibold text-white rounded-xl shadow-lg hover:scale-105 transition-transform"
          >
            Send Message ðŸš€
          </button>
        </form>

        <div className="text-center text-white mt-6 text-sm">
          ðŸ“ž Phone: +91 9876543210 <br />
          ðŸ“§ Email: support@example.com
        </div>
      </div>
    </div>
  );
};

export default Contact;
